
'use strict';
const proxy = require('http-proxy-middleware');
class Query_phone_local {
  	queryingLocation(req,res){
  		proxy({
			target: 'http://ejiaofei.wxpay.ejiaofei.cn/Handler/ShengHuoFuWu/CZ_SJ/queryRegion.ashx',
			//接口1：  http://www.ejiaofei.cn/Handler/ShengHuoFuWu/CZ_SJ/queryRegion.ashx  //返回对象
			//接口2：  http://ejiaofei.wxpay.ejiaofei.cn/Handler/ShengHuoFuWu/CZ_SJ/queryRegion.ashx  //返回字符串
			changeOrigin: true,
			pathRewrite: JSON.parse('{"'+ req.url + '":""}')
		})(req,res);
  	}
}
module.exports = Query_phone_local;